from PyQt5.QtCore import Qt
from PyQt5.QtWidgets import QApplication, QWidget

#___  ____ _ _ _ ____ ____    ___  _    ____ _  _ ___
#|__] |  | | | | |___ |__/    |__] |    |__| |\ |  |
#|    |__| |_|_| |___ |  \    |    |___ |  | | \|  |
                

#OPENING | https://www.youtube.com/watch?v=_85LaeTCtV8 :3



def enable_zoom(main_widget):
    # Enable zoom functionality on the given main_widget.
    # Pass the main_widget to this function to activate zooming.

    def scale_fonts(widget, scale_factor):
        # Apply the zoom factor to all font sizes of the widget and its children
        for child in widget.children():
            if isinstance(child, QWidget):
                font = child.font()
                font.setPointSizeF(font.pointSizeF() * scale_factor)
                child.setFont(font)
                scale_fonts(child, scale_factor)

    def wheelEvent(event):
        # Handle zooming with the mouse wheel (CTRL + wheel)
        if QApplication.keyboardModifiers() == Qt.ControlModifier:
            delta = event.angleDelta().y()
            if delta > 0:
                zoom(1.1)
            elif delta < 0:
                zoom(1 / 1.1)

    def keyPressEvent(event):
        # Handle zooming with the "+" and "-" keys
        if event.key() == Qt.Key_Plus:
            zoom(1.1)
        elif event.key() == Qt.Key_Minus:
            zoom(1 / 1.1)
        else:
            super(main_widget.__class__, main_widget).keyPressEvent(event)

    def zoom(scale_factor):
        # Apply the zoom factor to the main_widget
        scale_fonts(main_widget, scale_factor)

    # Override the main_widget's wheelEvent and keyPressEvent with the zooming functions
    main_widget.wheelEvent = wheelEvent
    main_widget.keyPressEvent = keyPressEvent
